import colors from "colors";
export function dummyLog() {
    // do nothing
}
export function prettyLog(context, message) {
    message = message.replace(/ \d+(x\d+)?/g, (item) => colors.magenta(item));
    message = message.replace(/#([0-9a-f]{3}){1,2}/g, (item) => colors.magenta(item));
    console.log(`${colors.green("[Favicons]")} ${colors.yellow(context)}: ${message}...`);
}
export function logContext(logger, context) {
    return (innerContext, message) => {
        logger(`${context}:${innerContext}`, message);
    };
}
